import React, { useState, useEffect } from "react";
import axios from "axios";
import BlogCard from "../components/BlogCard";
import { Grid, Card, CardHeader, CardContent, Typography } from "@mui/material";

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);

  const getAllBlogs = async () => {
    try {
      const { data } = await axios.get(
        "http://localhost:8000/api/v1/blog/all-blog"
      );
      if (data?.success) {
        setBlogs(data.blogs);
      }
    } catch (err) {
      console.log(err);
    }
  };
  // const url = blogs.image
  //   ? blogs.image
  //   : "https://images.unsplash.com/photo-1498050108023-c5249f4df085?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=752&q=80 ";

  useEffect(() => {
    getAllBlogs();
  }, []);
  console.log(blogs, "update");
  return (
    <div>
      <Grid item container lg={4}>
        <Card
          sx={{
            boxShadow: "5px 5px 10px #ccc",
            ":hover:": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <CardHeader
            title={blogs.title}
            subheader={blogs.time}
            sx={{ cursor: "pointer" }}
            // onClick={(e) => viewReport(e, `${_id}`)}
          />
          <CardHeader
            sx={{ cursor: "pointer", fontSize: "5px" }}
            title={blogs.categories}
          />
          <img
            height="217px"
            width="426px"
            src={`http://localhost:8000/${blogs.image}`}
            alt="blog image"
          />

          <CardContent>
            <Typography
              variant="body2"
              color="text.secondary"
              sx={{ cursor: "pointer" }}
            >
              {blogs.description}
            </Typography>
          </CardContent>
        </Card>
      </Grid>
    </div>
  );
};

export default Blogs;
