import React, { useState, useEffect } from "react";
// import { useLocation } from "react-router-dom";
import axios from "axios";
import { useParams } from "react-router-dom";
import {
  Card,
  Grid,
  CardHeader,
  Typography,
  CardMedia,
  CardContent,
  TextField,
  Button,
  IconButton,
} from "@mui/material";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";

const DetailsPage = () => {
  const [blog, setBlog] = useState({});
  const [comments, setComment] = useState([]);
  const id = useParams().id;

  const [input, setInput] = useState({
    title: "",
  });
  //get blog datails ;
  const getBlogDetails = async () => {
    try {
      const { data } = await axios.get(`/api/v1/blog/get-blog/${id}`);
      if (data?.success) {
        setBlog(data?.blog);
      }
    } catch (err) {
      console.log(err);
    }
  };

  const getcomment = async () => {
    try {
      await axios
        .get("http://127.0.0.1:8000/api/v1/comment/all-comment")
        .then((response) => setComment(response.data.comment))
        .catch((err) => console.log(err));
    } catch (err) {
      console.log(err);
    }
  };

  const handleChange = (e) => {
    setInput((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const handleDelete = async (id) => {
    await axios
      .delete(`/api/v1/blog/delete-blog/:id`)
      .then((response) => console.log(response));
  };

  const handleEdit = async () => {
    await axios
      .put(`/api/v1/blog/update-blog/:id`)
      .then((response) => console.log(response));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post(
        "http://127.0.0.1:8000/api/v1/comment/create-comment",
        {
          title: input.title,
        }
      );
      if (data.success) {
        alert("comment successfully");
      }
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getBlogDetails();
  }, [id]);

  useEffect(() => {
    getcomment();
  }, []);

  //   const location = useLocation();
  //   useEffect(() => {
  //     const id = location.pathname.split("/view/")[1];
  //     console.log(id);
  //     if(id){
  //       viewReport(id);
  //     }
  //   }, [location]);
  return (
    <div>
      <Grid>
        <Card
          sx={{
            boxShadow: "5px 5px 10px #ccc",
            ":hover:": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <DeleteIcon
            sx={{ cursor: "pointer" }}
            onClick={(e, id) => handleDelete(e, `${id}`)}
          />
          <EditIcon
            sx={{ cursor: "pointer" }}
            onClick={(e) => handleEdit(e, `${id}`)}
          />
          <CardHeader
            title={blog.title}
            subheader={blog.time}
            sx={{ cursor: "pointer" }}
          />
          <CardMedia
            component="img"
            height="194"
            image={blog.image}
            // alt="Paella dish"
          />
          <CardContent>
            <Typography
              variant="body2"
              color="text.secondary"
              sx={{ cursor: "pointer" }}
            >
              {blog.description}
            </Typography>
          </CardContent>
        </Card>
      </Grid>
      {comments?.map((data, index) => {
        return (
          <div key={index}>
            <Card
              sx={{
                marginTop: "15px",
                width: "50%",
                margin: "auto",
                height: "30px",
              }}
            >
              {data.title}
            </Card>
          </div>
        );
      })}
      <form onSubmit={handleSubmit}>
        <TextField
          onChange={handleChange}
          value={input.title}
          placeholder="comment"
          type={"text"}
          name="title"
          sx={{
            width: "70%",
            marginLeft: "150px",
            marginTop: "10px",
          }}
        />
        <Button
          type="submit"
          variant="contained"
          sx={{
            marginTop: "28px",
            marginLeft: "5px",
          }}
        >
          Send
        </Button>
      </form>
    </div>
  );
};

export default DetailsPage;
