import React, { useState, useEffect } from "react";
// import { useLocation } from "react-router-dom";
import axios from "axios";
import { useParams } from "react-router-dom";
import {
  Card,
  Grid,
  CardHeader,
  Typography,
  CardMedia,
  CardContent,
} from "@mui/material";

const DetailsPage = () => {
  const [blog, setBlog] = useState({});
  const [comments, setComment] = useState([]);
  const id = useParams().id;
  //get blog datails ;

  const getBlogDetails = async () => {
    try {
      const { data } = await axios.get(`/api/v1/blog/get-blog/${id}`);
      if (data?.success) {
        setBlog(data?.blog);
      }
    } catch (err) {
      console.log(err);
    }
  };

  const getcomment = async () => {
    try {
      const data = await axios
        .get("http://127.0.0.1:8000/api/v1/comment/all-comment")
        .then(function (res) {
          console.log(res.setComment);
        });
      //   setComment(data);
    } catch (err) {
      console.log(err);
    }
  };
  console.log(comments);

  useEffect(() => {
    getBlogDetails();
    getcomment();
  }, [id]);
  console.log(blog, "blogg22222");

  //   const location = useLocation();
  //   useEffect(() => {
  //     const id = location.pathname.split("/view/")[1];
  //     console.log(id);
  //     if(id){
  //       viewReport(id);
  //     }
  //   }, [location]);
  return (
    <div>
      <Grid>
        <Card
          sx={{
            boxShadow: "5px 5px 10px #ccc",
            ":hover:": {
              boxShadow: "10px 10px 20px #ccc",
            },
          }}
        >
          <CardHeader
            title={blog.title}
            subheader={blog.time}
            sx={{ cursor: "pointer" }}
          />
          <CardMedia
            component="img"
            height="194"
            image={blog.image}
            // alt="Paella dish"
          />
          <CardContent>
            <Typography
              variant="body2"
              color="text.secondary"
              sx={{ cursor: "pointer" }}
            >
              {blog.description}
            </Typography>
          </CardContent>
        </Card>
      </Grid>
      <Grid>
        {/* {comments &&
          comments.data.comment.map((commentt) => (
            <Card
              sx={{
                marginTop: "20px",
                width: "90%",
                margin: "auto",
              }}
            >
              {commentt.title}
            </Card>
          ))} */}
      </Grid>
    </div>
  );
};

export default DetailsPage;
